SELECT A.COMPANY, A.ITNBR, A.NAME ITEM_NAME_K, A.SPEC1 NORM1, A.UNMSR PUR_UM, B.CURRENCY, B.VENDOR, SF_BUYER_NM(B.VENDOR) VENDOR_NAME, B.SUPPLY, B.TEMP_PRICE, B.PAST_PRICE, B.LAST_PRICE, TO_CHAR(B.START_DATE, 'yyyy-mm-dd') START_DATE FROM SALE.SAB01_MST A, PAF03_MST B WHERE B.COMPANY = #{company_cd}
AND B.ITEM >= #{itnbr}
AND (B.VENDOR = #{by_cd} OR #{by_cd} = '*')
AND A.COMPANY = B.COMPANY AND A.ITNBR = B.ITEM AND ROWNUM < #{page} * 30+1 ORDER BY A.itnbr

SELECT GRPNO, PNLNO, ITDSC, SOQTY1, SOWPRC1 * DECODE(UPPER(VAT_YN), 'Y', 1.1, 1.0) SOWPRC1, SOWAMT1 * DECODE(UPPER(VAT_YN), 'Y', 1.1, 1.0) SOWAMT1, ITNBR, GU0, VAT_YN, COST_YN, PJTNO, COMPANY, SF_PJTNO_NM(#{company_cd}, '100', PJTNO) PJTNO_NM FROM ( SELECT GRPNO, PNLNO, ITDSC, SOQTY1, SOWPRC1, SOWAMT1, ITNBR, GU0, 'R_VAT_YN' AS VAT_YN, 'R_COST_YN' AS COST_YN, PJTNO, COMPANY FROM ( SELECT A.GRPNO, B.PNLNO, B.ITDSC, A.SOQTY1 * B.SOQTY1 AS SOQTY1, B.SOWPRC1 SOWPRC1, A.SOQTY1 * B.SOWAMT1 AS SOWAMT1, B.ITNBR, '2' AS GU0, A.PJTNO, A.COMPANY FROM ( SELECT GRPNO, ITNBR, ITDSC, SOQTY1, SOWPRC1_CONFIRM SOWPRC1, PJTNO, COMPANY FROM SCA02_MST WHERE COMPANY = #{company_cd}
AND PJTNO = #{pjtno}
AND GU0 = '1' ) A, ( SELECT GRPNO, PNLNO, ITNBR, ITDSC, SOQTY1, SOWPRC1_CONFIRM SOWPRC1, SOWAMT1, COMPANY FROM SCA02_MST WHERE COMPANY = #{company_cd}
AND PJTNO = #{pjtno}
AND GU0 = '2' AND SUBSTR(ITNBR, 1, 1) != 'M' UNION ALL SELECT B1.GRPNO, B1.PNLNO, B2.ITNBR, B2.ITDSC, B2.SOQTY1, 0, B1.SOWAMT1, B1.COMPANY FROM ( SELECT GRPNO, MIN(PNLNO) AS PNLNO, SUM(SOWAMT1) AS SOWAMT1, COMPANY FROM SCA02_MST WHERE COMPANY = #{company_cd}
AND PJTNO = #{pjtno}
AND GU0 = '2' AND SUBSTR(ITNBR, 1, 1) = 'M' GROUP BY GRPNO, COMPANY ) B1, SCA02_MST B2 WHERE B2.COMPANY = #{company_cd}
AND B2.PJTNO = #{pjtno}
AND B1.GRPNO = B2.GRPNO AND B1.PNLNO = B2.PNLNO AND B2.ITMNO = 0 ) B WHERE A.GRPNO = B.GRPNO UNION ALL SELECT GRPNO, PNLNO, ITDSC, 0, 0, 0, ' ', GU0, PJTNO, COMPANY FROM SCA02_MST WHERE COMPANY = #{company_cd}
AND PJTNO = #{pjtno}
AND GU0 = '1' ) ) ORDER BY GRPNO, PNLNO

SELECT A.COMPANY, A.PLANT, A.PJTNO, PROD.SF_PJTNO_DATA(A.COMPANY, A.PLANT, A.PJTNO, '1') PJTNM, A.CUST, PROD.SF_BUYER_NM(A.CUST) CUST_NM, A.ITEM, PROD.SF_ITNBR_NM(A.COMPANY, A.ITEM, '2') ITEM_NM, NVL(A.ORDER_QTY,0) ORDER_QTY, NVL(A.ORDER_AMT,0) ORDER_AMT, TO_CHAR(A.ORDER_DATE, 'YYYY-MM-DD') ORDER_DATE, TO_CHAR(A.DUE_DATE, 'YYYY-MM-DD') DUE_DATE, /*구매목표예산 정보 */ NVL(A.TOTAL_AMT,0) TOTAL_AMT, NVL(A.LASTTOTAL_AMT,0) LASTTOTAL_AMT, NVL(A.PUR_AMT,0) PUR_AMT, NVL(A.OUT_AMT,0) OUT_AMT, NVL(A.B_STAND_AMT,0) BUD_B_STAND_AMT, NVL(A.E_STAND_AMT,0) BUD_E_STAND_AMT, NVL(A.B_PROC_AMT,0) BUD_B_PROC_AMT, NVL(A.E_PROC_AMT,0) BUD_E_PROC_AMT, NVL(A.B_MAKE_AMT,0) BUD_B_MAKE_AMT, NVL(A.E_MAKE_AMT,0) BUD_E_MAKE_AMT, NVL(A.B_PAY_AMT,0) BUD_B_PAY_AMT, NVL(A.E_PAY_AMT,0) BUD_E_PAY_AMT, NVL(A.B_SUP_AMT,0) BUD_B_SUP_AMT, NVL(A.E_SUP_AMT,0) BUD_E_SUP_AMT, NVL(A.TRIP_AMT,0) BUD_TRIP_AMT, NVL(A.PACK_AMT,0) BUD_PACK_AMT, /*정상발주 내역 */ NVL(B.TOTAL_AMT,0) PUR_TOTAL_AMT, NVL(B.B_STAND_AMT,0) PUR_B_STAND_AMT, NVL(B.E_STAND_AMT,0) PUR_E_STAND_AMT, NVL(B.B_PROC_AMT,0) PUR_B_PROC_AMT, NVL(B.E_PROC_AMT,0) PUR_E_PROC_AMT, NVL(B.B_MAKE_AMT,0) PUR_B_MAKE_AMT, NVL(B.E_MAKE_AMT,0) PUR_E_MAKE_AMT, NVL(B.B_PAY_AMT,0) PUR_B_PAY_AMT, NVL(B.E_PAY_AMT,0) PUR_E_PAY_AMT, NVL(B.B_SUP_AMT,0) PUR_B_SUP_AMT, NVL(B.E_SUP_AMT,0) PUR_E_SUP_AMT, 0 PUR_TRIP_AMT, 0 PUR_PACK_AMT, /*정상출고 내역*/ NVL(C.TOTAL_AMT,0) OUT_TOTAL_AMT, NVL(C.B_STAND_AMT,0) OUT_B_STAND_AMT, NVL(C.E_STAND_AMT,0) OUT_E_STAND_AMT, NVL(C.B_PROC_AMT,0) OUT_B_PROC_AMT, NVL(C.E_PROC_AMT,0) OUT_E_PROC_AMT, NVL(C.B_MAKE_AMT,0) OUT_B_MAKE_AMT, NVL(C.E_MAKE_AMT,0) OUT_E_MAKE_AMT, NVL(C.B_PAY_AMT,0) OUT_B_PAY_AMT, NVL(C.E_PAY_AMT,0) OUT_E_PAY_AMT, NVL(C.B_SUP_AMT,0) OUT_B_SUP_AMT, NVL(C.E_SUP_AMT,0) OUT_E_SUP_AMT, 0 OUT_TRIP_AMT, 0 OUT_PACK_AMT, /*추가발주 내역 */ NVL(D.TOTAL_AMT,0) ADD_PUR_TOTAL_AMT, NVL(D.B_STAND_AMT,0) + NVL(F.B_STAND_ALLO_AMT,0) ADD_PUR_B_STAND_AMT, NVL(D.E_STAND_AMT,0) + NVL(F.E_STAND_ALLO_AMT,0) ADD_PUR_E_STAND_AMT, NVL(D.B_PROC_AMT,0) + NVL(F.B_PROC_ALLO_AMT,0) ADD_PUR_B_PROC_AMT, NVL(D.E_PROC_AMT,0) + NVL(F.E_PROC_ALLO_AMT,0) ADD_PUR_E_PROC_AMT, NVL(D.B_MAKE_AMT,0) + NVL(F.B_MAKE_ALLO_AMT,0) ADD_PUR_B_MAKE_AMT, NVL(D.E_MAKE_AMT,0) + NVL(F.E_MAKE_ALLO_AMT,0) ADD_PUR_E_MAKE_AMT, NVL(D.B_PAY_AMT,0) + NVL(F.B_PAY_ALLO_AMT,0) ADD_PUR_B_PAY_AMT, NVL(D.E_PAY_AMT,0) + NVL(F.E_PAY_ALLO_AMT,0) ADD_PUR_E_PAY_AMT, NVL(D.B_SUP_AMT,0) + NVL(F.B_SUP_ALLO_AMT,0) ADD_PUR_B_SUP_AMT, NVL(D.E_SUP_AMT,0) + NVL(F.E_SUP_ALLO_AMT,0) ADD_PUR_E_SUP_AMT, 0 ADD_PUR_TRIP_AMT, 0 ADD_PUR_PACK_AMT, /*추가출고 내역*/ NVL(E.TOTAL_AMT,0) ADD_OUT_TOTAL_AMT, NVL(E.B_STAND_AMT,0) ADD_OUT_B_STAND_AMT, NVL(E.E_STAND_AMT,0) ADD_OUT_E_STAND_AMT, NVL(E.B_PROC_AMT,0) ADD_OUT_B_PROC_AMT, NVL(E.E_PROC_AMT,0) ADD_OUT_E_PROC_AMT, NVL(E.B_MAKE_AMT,0) ADD_OUT_B_MAKE_AMT, NVL(E.E_MAKE_AMT,0) ADD_OUT_E_MAKE_AMT, NVL(E.B_PAY_AMT,0) ADD_OUT_B_PAY_AMT, NVL(E.E_PAY_AMT,0) ADD_OUT_E_PAY_AMT, NVL(E.B_SUP_AMT,0) ADD_OUT_B_SUP_AMT, NVL(E.E_SUP_AMT,0) ADD_OUT_E_SUP_AMT, 0 ADD_OUT_TRIP_AMT, 0 ADD_OUT_PACK_AMT, (SELECT NVL(SUM(NVL(QTYPR,0) * NVL(STD_PRICE,0)),0) FROM PROD.PDH02_TBL WHERE COMPANY = A.COMPANY AND PLANT = A.PLANT AND PJTNO = A.PJTNO) PROD_EXP_AMT, TO_CHAR(A.FIX_DATE, 'YYYY-MM-DD') PLAN_MK_DATE, (SELECT nvl(SUM(NVL(allo_qty,0) * NVL(pur_price,0)),0) FROM pda04_dtl WHERE company = A.company AND plant = A.plant AND pdtno = a.pjtno AND NVL(ALLO_QTY,0) <> 0 /* AND NVL(PR_STAT,'X') <> 'C' */ ) ALLO_AMT FROM PROD.PDH01_MST A, /*구매목표마스타*/ PROD.PDH01_VIW01 B, /*정상발주*/ PROD.PDH01_VIW02 C, /*정상출고*/ PROD.PDH01_VIW03 D, /*추가발주*/ PROD.PDH01_VIW04 E, /*추가출고*/ PROD.PDH01_VIW05 F /*할당사용*/ WHERE A.COMPANY = #{company_cd} AND A.PLANT = #{plant_cd}
AND SUBSTR(A.PJTNO,3,6) BETWEEN SUBSTR(#{pjtno_fr},3,6) AND SUBSTR(#{pjtno_to},3,6)
AND A.COMPANY = B.COMPANY(+) AND A.PLANT = B.PLANT(+) AND A.PJTNO = B.PJTNO(+) AND A.COMPANY = C.COMPANY(+) AND A.PLANT = C.PLANT(+) AND A.PJTNO = C.PJTNO(+) AND A.COMPANY = D.COMPANY(+) AND A.PLANT = D.PLANT(+) AND A.PJTNO = D.PJTNO(+) AND A.COMPANY = E.COMPANY(+) AND A.PLANT = E.PLANT(+) AND A.PJTNO = E.PJTNO(+) AND A.COMPANY = F.COMPANY(+) AND A.PLANT = F.PLANT(+) AND A.PJTNO = F.PJTNO(+) AND NVL(A.STATUS,'XX') = '90'

SELECT B.COMPANY, B.PLANT, B.PJTNO, D.PJTNM, D.CVCOD, B.ITEM, C.NAME, C.SPEC1, C.UNMSR, B.OBJ_PRICE, B.STD_PRICE, B.PUR_PRICE, B.BUD_QTY, B.BUD_AMT, B.PL_QTY, B.PL_AMT, B.PUR_QTY, B.PUR_AMT FROM( SELECT A.COMPANY, A.PLANT, A.PJTNO, A.ITEM, MAX(A.OBJ_PRICE) OBJ_PRICE , MAX(A.STD_PRICE) STD_PRICE , MAX(A.PUR_PRICE) PUR_PRICE , SUM(A.BUD_QTY) BUD_QTY , SUM(A.BUD_AMT) BUD_AMT , SUM(A.PL_QTY) PL_QTY , SUM(A.PL_AMT) PL_AMT , SUM(A.PUR_QTY) PUR_QTY , SUM(A.PUR_AMT) PUR_AMT FROM ( SELECT K.COMPANY, K.PLANT, K.PJTNO, K.CINBR ITEM, NVL(K.OBJ_PRICE,0) OBJ_PRICE, NVL(SUM(NVL(K.QTYPR,0)),0) BUD_QTY, NVL(SUM(NVL(K.QTYPR,0) * NVL(OBJ_PRICE,0)),0) BUD_AMT, 0 STD_PRICE, 0 PL_QTY, 0 PL_AMT, 0 PUR_PRICE, 0 PUR_QTY, 0 PUR_AMT FROM PROD.PDH02_TBL K, PROD.PDH01_MST L WHERE K.COMPANY = L.COMPANY AND K.PLANT = L.PLANT AND K.PJTNO = L.PJTNO AND K.COMPANY = #{company_cd} AND K.PLANT = #{plant_cd}
AND SUBSTR(K.PJTNO,3,6) BETWEEN SUBSTR(#{pjtno_fr},3,6) AND SUBSTR(#{pjtno_to},3,6)
AND NVL(L.STATUS,'XX') = '90' GROUP BY K.COMPANY, K.PLANT, K.PJTNO, K.CINBR, NVL(K.OBJ_PRICE,0) UNION ALL SELECT K.COMPANY, K.PLANT, K.PDTNO PJTNO, K.CINBR ITEM, 0 OBJ_PRICE, 0 BUD_QTY, 0 BUD_AMT, NVL(L.STD_PRICE,0) STD_PRICE, NVL(SUM(NVL(K.QTYPR,0)),0) PL_QTY, NVL(SUM(NVL(K.QTYPR,0) * NVL(L.STD_PRICE,0)),0) PL_AMT, 0 PUR_PRICE, 0 PUR_QTY, 0 PUR_AMT FROM PROD.PAE03_MST K, PROD.PAE03_HIS J, PROD.PAD01_MST L WHERE K.COMPANY = #{company_cd} AND K.PLANT = #{plant_cd}
AND SUBSTR(K.PDTNO,3,6) BETWEEN SUBSTR(#{pjtno_fr},3,6) AND SUBSTR(#{pjtno_to},3,6)
AND K.COMPANY = J.COMPANY AND K.PLANT = J.PLANT AND K.PDTNO = J.PDTNO AND K.DRSEQ = J.DRSEQ AND J.CONF_DATE IS NOT NULL AND K.COMPANY = L.COMPANY(+) AND K.CINBR = L.ITNBR(+) GROUP BY K.COMPANY, K.PLANT, K.PDTNO, K.CINBR, NVL(L.STD_PRICE,0) UNION ALL SELECT TB.COMPANY, TB.PLANT, TB.PDTNO PJTNO, TB.ITEM, 0 OBJ_PRICE, 0 BUD_QTY, 0 BUD_AMT, 0 STD_PRICE, 0 PL_QTY, 0 PL_AMT, NVL(TB.PUR_PRICE,0) PUR_PRICE, NVL(SUM(NVL(TB.PUR_QTY,0) - NVL(TB.CNCL_QTY,0)),0) PUR_QTY, NVL(SUM((NVL(TB.PUR_QTY,0) - NVL(TB.CNCL_QTY,0)) * NVL(TB.PUR_PRICE,0)),0) PUR_AMT FROM PROD.PDA04_MST TA, PROD.PDA04_DTL TB WHERE TA.COMPANY = #{company_cd} AND TA.PLANT = #{plant_cd}
AND SUBSTR(TA.PDTNO,3,6) BETWEEN SUBSTR(#{pjtno_fr},3,6) AND SUBSTR(#{pjtno_to},3,6)
AND TA.COMPANY = TB.COMPANY AND TA.PLANT = TB.PLANT AND TA.PDTNO = TB.PDTNO AND TA.DRSEQ = TB.DRSEQ AND TA.PUR_NO = TB.PUR_NO AND NVL(TB.PUR_QTY,0) - NVL(TB.CNCL_QTY,0) != 0 GROUP BY TB.COMPANY, TB.PLANT, TB.PDTNO, TB.ITEM, NVL(TB.PUR_PRICE,0) ) A GROUP BY A.COMPANY, A.PLANT, A.PJTNO, A.ITEM ) B, SALE.SAB01_MST C, SALE.SCA09_MST D WHERE B.COMPANY = D.COMPANY(+) AND B.PLANT = D.PLANT(+) AND B.PJTNO = D.PJTNO(+) AND B.COMPANY = C.COMPANY(+) AND B.ITEM = C.ITNBR(+) ORDER BY B.COMPANY, B.PLANT, B.PJTNO, B.ITEM

SELECT  COMPANY,
	        PLANT,
	        PJTNO,
	        GUBUN,
	        PJT_SEQ,
	        NVL(IN_AMT,0) AMT,
	        TRIM(LPAD(DECODE(GUBUN,'A0','수주금액','B0','목표예산','C0','요구금액','D0','발주금액','E0','재고사용','F0','자재합계','G0','FAIL COST','비율(%)'),10)) GBN_NM,
	        (SELECT NVL(MAX(PJT_SEQ),0)
	         FROM PDD06_TMP01
	         WHERE COMPANY = #{company_cd}
	         AND PLANT = #{plant_cd}
	         AND PJT_SEQ  <=  17
	         AND USER_ID = #{user_id} 
	         AND SUBSTR(PJTNO,1,3) NOT IN ('TMP','TOT')
	        ) MAX_SEQ,
	        USER_ID
FROM PDD06_TMP01
WHERE COMPANY = #{company_cd}
AND PLANT = #{plant_cd}
AND PJT_SEQ  <=  17

	AND USER_ID = #{user_id} 

ORDER BY COMPANY, PLANT, GUBUN, PJT_SEQ, PJTNO

SELECT  COMPANY,
	        PLANT,
	        YYMM,
	        DECODE(YYMM,'TOTAL', YYMM, SUBSTR(YYMM,5,2) || '월') MONTH,
	        GUBUN,
	        NVL(IN_AMT,0) AMT,
	        TRIM(LPAD(DECODE(GUBUN,'A0','수주금액','B0','목표예산','C0','요구금액','D0','발주금액','E0','재고사용','F0','자재합계','G0','FAIL COST','비율(%)'),10)) GBN_NM,
	        (SELECT NVL(MAX(SUBSTR(YYMM,5,2)),'00')
	         FROM PDD06_TMP02
	         WHERE COMPANY = #{company_cd}
	         AND PLANT = #{plant_cd}
	         AND USER_ID = #{user_id} 
	         AND YYMM  <>  'TOTAL'
	         AND GUBUN = 'A0'
	         AND NVL(IN_AMT,0)    > 0
	        ) MAX_YYMM,
	        USER_ID
FROM PDD06_TMP02
WHERE COMPANY = #{company_cd}
AND PLANT = #{plant_cd}
AND USER_ID = #{user_id} 
ORDER BY COMPANY, PLANT, GUBUN, YYMM

SELECT A.COMPANY, A.PLANT, A.PDTNO, SF_PJTNO_DATA(A.COMPANY, A.PLANT, SUBSTR(A.PDTNO,1,8), '1') PJTNO_NM, SF_DRSEQ_DRDSC(A.COMPANY, A.PLANT, SUBSTR(A.PDTNO,1,9), A.DRSEQ) DRDSC, A.DRSEQ, A.PUR_NO, B.PUR_SEQ, B.ITEM, B.ITEM_NAME, B.NORM, B.UOM, A.SUPL_CLASS, B.CURRENCY, A.VENDOR, C.BY_NM, A.PUR_DATE, A.PAY_METHOD, A.PUR_TYPE, NVL(B.PUR_QTY, 0) - NVL(B.CNCL_QTY, 0) PUR_QTY, NVL(B.PUR_PRICE,0) PUR_PRICE, ((NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY, 0)) * NVL(B.PUR_PRICE,0)) PUR_AMT, NVL(B.NEGO_PRICE,0) NEGO_PRICE, B.DELV_DATE, NVL(B.ALLO_QTY,0) ALLO_QTY FROM PDA04_MST A, PDA04_DTL B, ACNT.SAA_BUYER C, SCA09_MST F WHERE A.COMPANY = #{company_cd} AND A.PLANT = #{plant_cd}
AND TRUNC(A.PUR_DATE) BETWEEN REPLACE(substr(#{date_fr}, 1, 10), '-', '') AND REPLACE(substr(#{date_to}, 1, 10), '-', '')
AND A.VENDOR BETWEEN #{cust_cd1} AND #{cust_cd2}
AND SUBSTR(A.PDTNO,3,8) BETWEEN SUBSTR(#{pjtno1},3,8) AND SUBSTR(#{pjtno2},3,8)
AND B.ITEM BETWEEN #{itnbr1} AND #{itnbr2}
AND A.PUR_NO BETWEEN #{purno1} AND #{purno2}
AND ((#{itnbr_gu} = '%') OR (#{itnbr_gu} = 'M' AND((A.DRSEQ BETWEEN '100' AND '199') OR (A.DRSEQ BETWEEN '300' AND '399')) AND SUBSTR(F.PJTNO,2,1) <> 'T') OR (#{itnbr_gu} = 'E' AND(A.DRSEQ BETWEEN '200' AND '299') AND SUBSTR(F.PJTNO,2,1) <> 'T') OR (#{itnbr_gu} = 'C' AND SUBSTR(F.PJTNO,2,1) = 'T'))
AND A.COMPANY = B.COMPANY(+) AND A.PLANT = B.PLANT(+) AND A.PDTNO = B.PDTNO(+) AND A.DRSEQ = B.DRSEQ(+) AND A.PUR_NO = B.PUR_NO(+) AND A.VENDOR = C.BY_CD(+) AND A.COMPANY = F.COMPANY(+) AND A.PLANT = F.PLANT(+) AND A.PDTNO = F.PJTNO(+) ORDER BY A.PUR_NO, B.ITEM

SELECT A.COMPANY, A.PLANT, A.PDTNO, B.ITEM, MAX(NVL(B.PUR_PRICE,0)) PUR_PRICE, MAX(NVL(B.NEGO_PRICE,0)) NEGO_PRICE, SUM(NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) PUR_QTY, SUM((NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) * NVL(B.PUR_PRICE,0)) PUR_AMT, SF_PJTNO_DATA(A.COMPANY, A.PLANT, SUBSTR(A.PDTNO,1,8), '1') PJTNO_NM, SF_ITNBR_TOT2(A.COMPANY, B.ITEM, '2', '1') ITEM_NAME, SF_ITNBR_TOT2(A.COMPANY, B.ITEM, '2', '2') NORM, SF_ITNBR_TOT2(A.COMPANY, B.ITEM, '2', '5') UOM FROM PDA04_MST A, PDA04_DTL B, SCA09_MST F WHERE A.COMPANY = #{company_cd} AND A.PLANT = #{plant_cd} AND TRUNC(A.PUR_DATE) BETWEEN REPLACE(substr(#{date_fr}, 1, 10), '-', '') AND REPLACE(substr(#{date_to}, 1, 10), '-', '')
AND A.VENDOR BETWEEN #{cust_cd1} AND #{cust_cd2}
AND SUBSTR(A.PDTNO,3,8) BETWEEN SUBSTR(#{pjtno1},3,8) AND SUBSTR(#{pjtno2},3,8)
AND B.ITEM BETWEEN #{itnbr1} AND #{itnbr2}
AND A.PUR_NO BETWEEN #{purno1} AND #{purno2}
AND ((#{itnbr_gu} = '%') OR (#{itnbr_gu} = 'M' AND((A.DRSEQ BETWEEN '100' AND '199') OR (A.DRSEQ BETWEEN '300' AND '399')) AND SUBSTR(F.PJTNO,2,1) <> 'T') OR (#{itnbr_gu} = 'E' AND(A.DRSEQ BETWEEN '200' AND '299') AND SUBSTR(F.PJTNO,2,1) <> 'T') OR (#{itnbr_gu} = 'C' AND SUBSTR(F.PJTNO,2,1) = 'T')) AND A.COMPANY = B.COMPANY(+) AND A.PLANT = B.PLANT(+) AND A.PDTNO = B.PDTNO(+) AND A.DRSEQ = B.DRSEQ(+) AND A.PUR_NO = B.PUR_NO(+) AND A.COMPANY = F.COMPANY(+) AND A.PLANT = F.PLANT(+) AND A.PDTNO = F.PJTNO(+) GROUP BY A.COMPANY, A.PLANT, A.PDTNO, B.ITEM ORDER BY A.PDTNO, B.ITEM

SELECT A.COMPANY, A.PLANT, A.VENDOR, SUM(NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) PUR_QTY, SUM(NVL(B.ALLO_QTY,0) * NVL(B.PUR_PRICE,0)) ALLO_AMT, SUM((NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) * NVL(B.NEGO_PRICE,0)) NEGO_AMT, SUM((NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) * NVL(B.PUR_PRICE,0)) PUR_AMT, NVL(MAX(NVL(C.BY_NM,'')),'') BY_NM FROM PDA04_MST A, PDA04_DTL B, ACNT.SAA_BUYER C, SCA09_MST F WHERE A.COMPANY = #{company_cd} AND A.PLANT = #{plant_cd} AND TRUNC(A.PUR_DATE) BETWEEN REPLACE(substr(#{date_fr}, 1, 10), '-', '') AND REPLACE(substr(#{date_to}, 1, 10), '-', '')
AND A.VENDOR BETWEEN #{cust_cd1} AND #{cust_cd2}
AND SUBSTR(A.PDTNO,3,8) BETWEEN SUBSTR(#{pjtno1},3,8) AND SUBSTR(#{pjtno2},3,8)
AND B.ITEM BETWEEN #{itnbr1} AND #{itnbr2}
AND A.PUR_NO BETWEEN #{purno1} AND #{purno2}
AND ((#{itnbr_gu} = '%') OR (#{itnbr_gu} = 'M' AND((A.DRSEQ BETWEEN '100' AND '199') OR (A.DRSEQ BETWEEN '300' AND '399')) AND SUBSTR(F.PJTNO,2,1) <> 'T') OR (#{itnbr_gu} = 'E' AND(A.DRSEQ BETWEEN '200' AND '299') AND SUBSTR(F.PJTNO,2,1) <> 'T') OR (#{itnbr_gu} = 'C' AND SUBSTR(F.PJTNO,2,1) = 'T')) AND A.COMPANY = B.COMPANY(+) AND A.PLANT = B.PLANT(+) AND A.PDTNO = B.PDTNO(+) AND A.DRSEQ = B.DRSEQ(+) AND A.PUR_NO = B.PUR_NO(+) AND A.VENDOR = C.BY_CD(+) AND A.COMPANY = F.COMPANY(+) AND A.PLANT = F.PLANT(+) AND A.PDTNO = F.PJTNO(+) GROUP BY A.COMPANY, A.PLANT, A.VENDOR ORDER BY A.VENDOR

SELECT A.COMPANY, A.PLANT, A.PDTNO, SUM(NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) PUR_QTY, SUM(NVL(B.ALLO_QTY,0) * NVL(B.PUR_PRICE,0)) ALLO_AMT, SUM((NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) * NVL(B.NEGO_PRICE,0)) NEGO_AMT, SUM((NVL(B.PUR_QTY,0) - NVL(B.CNCL_QTY,0)) * NVL(B.PUR_PRICE,0)) PUR_AMT, SF_PJTNO_DATA(A.COMPANY, A.PLANT, SUBSTR(A.PDTNO,1,8), '1') PJTNO_NM FROM PDA04_MST A, PDA04_DTL B, SCA09_MST F WHERE A.COMPANY =#{company_cd} AND A.PLANT = #{plant_cd} AND TRUNC(A.PUR_DATE) BETWEEN REPLACE(substr(#{date_fr}, 1, 10), '-', '') AND REPLACE(substr(#{date_to}, 1, 10), '-', '')
AND A.VENDOR BETWEEN #{cust_cd1} AND #{cust_cd2}
AND SUBSTR(A.PDTNO,3,6) BETWEEN SUBSTR(#{pjtno1},3,6) AND SUBSTR(#{pjtno2},3,6)
AND B.ITEM BETWEEN #{itnbr1} AND #{itnbr2}
AND A.PUR_NO BETWEEN #{purno1} AND #{purno2}
AND ((#{itnbr_gu} = '%') OR (#{itnbr_gu} = 'M' AND ((A.DRSEQ BETWEEN '100' AND '199') OR (A.DRSEQ BETWEEN '300' AND '399')) AND SUBSTR(F.PJTNO,2,1) <> 'T' ) OR (#{itnbr_gu} = 'E' AND(A.DRSEQ BETWEEN '200' AND '299') AND SUBSTR(F.PJTNO,2,1) <> 'T' ) OR (#{itnbr_gu} = 'C' AND SUBSTR(F.PJTNO,2,1) = 'T' ) ) AND A.COMPANY = B.COMPANY(+) AND A.PLANT = B.PLANT(+) AND A.PDTNO = B.PDTNO(+) AND A.DRSEQ = B.DRSEQ(+) AND A.PUR_NO = B.PUR_NO(+) AND A.COMPANY = F.COMPANY(+) AND A.PLANT = F.PLANT(+) AND A.PDTNO = F.PJTNO(+) GROUP BY A.COMPANY, A.PLANT, A.PDTNO ORDER BY A.PDTNO

SELECT TO_CHAR(A.DEPS_DATE, 'YYYY-MM-DD') DEPS_DATE, A.ITEM, B.NAME ITEM_NAME_K, B.SPEC1 NORM1, SF_ITNBR_TOT(A.COMPANY, A.ITEM, '5') STOCK_UM, B.IFORM ITEM_TYPE, A.DEPS_TYPE, A.DEPS_QTY, A.DEPS_PRICE, NVL(A.DEPS_AMT, 0) DEPS_AMT, NVL(A.DEPS_FAMT, 0) DEPS_FAMT, A.PLANT, A.VENDOR VENDOR, A.VENDOR VENDOR_NAME, A.DEPS_NO, A.SUPL_CLASS, A.CURRENCY, TO_CHAR(A.DELV_DATE, 'YYYY-MM-DD') DELV_DATE, B.JIJIC, B.JEJOS, A.PDTNO, SF_PROJECT_NM(A.COMPANY,A.PLANT,A.PDTNO) PJT_NM, A.PUR_NO, SF_ITNBR_TOT(A.COMPANY, A.ITEM, '9') ABC_CD, C.BY_NM, A.COMPANY FROM PEB01_MST A, SALE.SAB01_MST B, ACNT.SAA_BUYER C WHERE A.COMPANY = #{company_cd} AND A.PLANT = #{plant_cd}
AND A.DEPS_DATE BETWEEN REPLACE(substr(#{date1}, 1, 10), '-', '') AND REPLACE(substr(#{date2}, 1, 10), '-', '')
AND A.SUPL_CLASS <> 'OS' AND A.STEP_STATUS = '50' AND B.COMPANY = A.COMPANY AND B.ITNBR = A.ITEM AND A.VENDOR = C.BY_CD ORDER BY A.DEPS_DATE, A.PDTNO, A.VENDOR, A.ITEM

SELECT A.COMPANY, A.PLANT, A.PJTNO, SF_PJTNO_DATA(COMPANY, PLANT, A.PJTNO,'1') PJTNO_NM, A.DRSEQ, A.ITEM, A.ITEM_SEQ, NVL(A.PL_QTY ,0) PL_QTY, A.PUR_NO, A.PUR_SEQ, NVL(A.ALLO_QTY,0) ALLO_QTY, NVL(A.PUR_QTY,0) PUR_QTY, NVL(A.REM_QTY,0) REM_QTY, A.DEPS_NO, NVL(A.DELV_QTY,0) DELV_QTY, NVL(A.DEPS_QTY,0) DEPS_QTY, A.OUT_NO, NVL(A.OUT_REQ_QTY,0) OUT_REQ_QTY, NVL(A.OUT_QTY,0) OUT_QTY, SF_PJTNO_DATA(A.COMPANY, A.PLANT, A.PJTNO,'1') PJT_NM, SF_ITNBR_TOT2(A.COMPANY, A.ITEM,'2','1') ITEM_NM, SF_ITNBR_TOT2(A.COMPANY, A.ITEM,'2','2') ITEM_SPEC, (SELECT VENDOR FROM PROD.PDA04_MST WHERE COMPANY = A.COMPANY AND PLANT = A.PLANT AND PDTNO = A.PJTNO AND DRSEQ = A.DRSEQ AND PUR_NO = A.PUR_NO) PUR_VENDOR, (SELECT VENDOR FROM PROD.PEB01_MST WHERE COMPANY = A.COMPANY AND PLANT = A.PLANT AND DEPS_NO = A.DEPS_NO AND ITEM = A.ITEM) DEPS_VENDOR FROM PROD.PEC12_TMP01 A WHERE COMPANY = #{company} AND PLANT = #{plant_cd}
AND SUBSTR(PJTNO,3,6) BETWEEN SUBSTR(#{pjtno1},3,6) AND SUBSTR(#{pjtno2},3,6)
AND DRSEQ BETWEEN #{drseq} AND #{drseq2}
AND ITEM BETWEEN #{item} AND #{item2}
ORDER BY A.PJTNO, A.DRSEQ, A.ITEM, A.ITEM_SEQ

SELECT A.COMPANY, A.PLANT, A.PJTNO, SF_PJTNO_DATA(COMPANY, PLANT, A.PJTNO, '1') PJTNO_NM, A.DRSEQ, A.ITEM, NVL(SUM(NVL(A.PL_QTY ,0)),0) PL_QTY, NVL(SUM(NVL(A.ALLO_QTY,0)),0) ALLO_QTY, NVL(SUM(NVL(A.PUR_QTY,0)),0) PUR_QTY, NVL(SUM(NVL(A.REM_QTY,0)),0) REM_QTY, NVL(SUM(NVL(A.DELV_QTY,0)),0) DELV_QTY, NVL(SUM(NVL(A.DEPS_QTY,0)),0) DEPS_QTY, NVL(SUM(NVL(A.OUT_REQ_QTY,0)),0) OUT_REQ_QTY, NVL(SUM(NVL(A.OUT_QTY,0)),0) OUT_QTY, SF_PJTNO_DATA(A.COMPANY, A.PLANT, A.PJTNO, '1') PJT_NM, SF_ITNBR_TOT2(A.COMPANY, A.ITEM, '2', '1') ITEM_NM, SF_ITNBR_TOT2(A.COMPANY, A.ITEM, '2', '2') ITEM_SPEC FROM PROD.PEC12_TMP01 A WHERE COMPANY = #{company} AND PLANT = #{plant_cd}
AND SUBSTR(PJTNO,3,6) BETWEEN SUBSTR(#{pjtno1},3,6) AND SUBSTR(#{pjtno2},3,6)
AND DRSEQ BETWEEN #{drseq} AND #{drseq2}
AND ITEM BETWEEN #{item} AND #{item2}
GROUP BY A.COMPANY, A.PLANT,A.PJTNO, A.DRSEQ, A.ITEM ORDER BY A.PJTNO, A.DRSEQ, A.ITEM

SELECT A.YYYYMM, A.ITEM, B.NAME ITEM_NAME_K, B.SPEC1, B.JIJIC, SF_ITNBR_TOT(B.COMPANY, B.ITNBR, '10') STOCK_UM, A.STOCK_PRICE, A.LASTDEPS_DATE, A.LASTOUT_DATE, B.JEJOS, SUM(A.BASIC_QTY) BASIC_QTY, SUM(A.BASIC_AMT) BASIC_AMT, SUM(A.DEPS_QTY) DEPS_QTY, SUM(A.DEPS_AMT) DEPS_AMT, SUM(A.OUT_QTY) OUT_QTY, DECODE(SUM(A.OUT_QTY),0,0,SUM(A.OUT_AMT)) OUT_AMT, DECODE(SUM(A.OUT_QTY),0,SUM(A.OUT_AMT),0) OUT_AMT_1, DECODE(SUM(A.STOCK_AMT),0,0,SUM(A.STOCK_QTY)) STOCK_QTY, SUM(A.STOCK_AMT) STOCK_AMT, C.AGE_PRICE, B.COMPANY FROM SALE.SAB01_MST B, PAD01_MST C, (SELECT YYYYMM, ITEM, TRUNC(STOCK_PRICE, 4) STOCK_PRICE, LASTDEPS_DATE, LASTOUT_DATE, SUM(NVL(BASIC_QTY,0)) BASIC_QTY, SUM(NVL(BASIC_AMT,0)) BASIC_AMT, SUM(NVL(DEPS_QTY,0)) DEPS_QTY, SUM(NVL(DEPS_AMT,0)) DEPS_AMT, SUM(NVL(OUT_QTY,0)) OUT_QTY, SUM(NVL(OUT_AMT,0)) OUT_AMT, SUM(NVL(STOCK_QTY,0)) STOCK_QTY, SUM(NVL(STOCK_AMT,0)) STOCK_AMT FROM PROD.PEG01_MST WHERE COMPANY = #{company_cd} AND PLANT = #{plant_cd}
AND YYYYMM = REPLACE(substr(#{yyyymm}, 1, 7), '-', '')
GROUP BY YYYYMM, ITEM, STOCK_PRICE, LASTDEPS_DATE, LASTOUT_DATE, COMPANY) A WHERE B.COMPANY = #{company_cd} AND A.ITEM = B.ITNBR AND B.ITNBR = C.ITNBR AND SUBSTR(B.IFORM,1,1) = 'A' AND (A.BASIC_QTY <> 0 OR A.DEPS_QTY <> 0 OR A.OUT_QTY <> 0 OR A.STOCK_QTY <> 0 ) AND A.ITEM <> '000000000' GROUP BY A.YYYYMM, A.ITEM, B.NAME, B.SPEC1, B.JIJIC, SF_ITNBR_TOT(B.COMPANY, B.ITNBR, '10'), A.STOCK_PRICE,A.LASTDEPS_DATE,A.LASTOUT_DATE,B.JEJOS,C.AGE_PRICE, B.COMPANY ORDER BY A.ITEM, A.YYYYMM

SELECT B.VENDOR VENDOR_NAME, B.INSP_DATE, B.PLANT, A.ITEM, C.NAME, C.SPEC1 NORM1, SF_ITNBR_TOT(A.COMPANY, A.ITEM, '10') STOCK_UM, SUM(A.DELV_QTY) DELV_QTY, SUM(A.INSP_QTY) INSP_QTY, SUM(A.IQC_QTY) IQC_QTY, SUM(A.ERR_QTY) ERR_QTY, B.COMPANY FROM PROD.PHA01_DTL A, PROD.PHA01_MST B, SALE.SAB01_MST C WHERE B.COMPANY = #{company_cd} AND B.PLANT = #{plant_cd}
AND B.INSP_DATE BETWEEN REPLACE(substr(#{insp_date_fr}, 1, 10), '-', '') AND REPLACE(substr(#{insp_date_to}, 1, 10), '-', '')
AND A.ITEM BETWEEN #{item1} AND #{item2}
AND A.COMPANY = B.COMPANY AND A.PLANT = B.PLANT AND A.INSP_NO = B.INSP_NO AND C.COMPANY = A.COMPANY AND C.ITNBR = A.ITEM GROUP BY B.VENDOR, B.INSP_DATE, B.PLANT, A.ITEM, C.NAME, C.SPEC1, SF_ITNBR_TOT(A.COMPANY, A.ITEM, '10') , B.COMPANY ORDER BY B.VENDOR, B.INSP_DATE, B.PLANT, A.ITEM
